                  <div className="mt-3 flex flex-wrap gap-2">
                    {meds.length === 0 && <div className="text-sm text-muted-foreground">No medications selected.</div>}
                    {meds.map((m) => (
                      <Badge key={m} className="flex items-center gap-2">
                        <span>{m}</span>
                        {isEditing && (
